import React, { useState, useEffect, useCallback } from 'react';

import MoviesList from './components/MoviesList';
import AddMovie from './components/AddMovie';
import './App.css';
import useHttp from './hooks/use-http';

function App() {
  const [movies, setMovies] = useState([]);
  const {isLoading, error, sendRequest: fetchMovies} = useHttp();

  const transfromMoviesData = (data) => {
    const loadedMovies = [];

    for (const key in data) {
      console.log(key);
      loadedMovies.push({
        id: key,
        title: data[key].title,
        openingText: data[key].openingText,
        releaseDate: data[key].releaseDate
      });
    }
    setMovies(loadedMovies);
  }
  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://react-http-7bb32-default-rtdb.firebaseio.com/movies.json');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();
      const loadedMovies = [];

      for (const key in data) {
        console.log(key);
        loadedMovies.push({
          id: key,
          title: data[key].title,
          openingText: data[key].openingText,
          releaseDate: data[key].releaseDate
        });
      }
      setMovies(loadedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMovies(
      { url: 'https://react-http-7bb32-default-rtdb.firebaseio.com/movies.json' },
      transfromMoviesData
    );
  }, [fetchMovies]);

  async function addMovieHandler(movie) {
    try {
      const response = await fetch('https://react-http-7bb32-default-rtdb.firebaseio.com/movies.json', {
        method: 'POST',
        body: JSON.stringify(movie),
        header: {
          'Content-Type': 'application/json'
        }
      });
      if (!response.ok) {
        throw new Error('Unable to add new movie');
      }
      const data = await response.json();
      setMovies(prevState => (
        [
          ...prevState,
          {
            id: data['name'],
            title: movie.title,
            openingText: movie.openingText,
            releaseDate: movie.releaseDate
          }]
      ))
    } catch (error) {
      setError(error.message);
    }
  }

  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
